import React, { useState } from 'react';
import "./productLIst.css";
import elec1 from "../images/brand-01.png";
import elec2 from "../images/brand-02.png";
import elec3 from "../images/brand-03.png";
import elec4 from "../images/brand-04.png";
import elec5 from "../images/brand-05.png";
import elec6 from "../images/brand-06.png";
import elec7 from "../images/brand-07.png";
import elec8 from "../images/brand-08.png";

import tab1 from "../images/tab.jpg";
import tab2 from "../images/tab1.jpg";
import tab3 from "../images/tab2.jpg";
import tab4 from "../images/tab3.jpg";

import "../images/camera.jpg"
import "../images/headphone.jpg";

const products = [

  { id: 1, category: 'clothing', img: tab1},
  { id: 2, category: 'clothing', img: tab2 },
  { id: 3, category: 'clothing', img: tab3 },
  { id: 4, category: 'clothing', img: tab4 },

  { id: 11, category: 'electronics', img: elec1 },
  { id: 12, category: 'electronics', img: elec2},
  { id: 13, category: 'electronics', img: elec3 },
  { id: 14, category: 'electronics', img: elec4},
  { id: 15, category: 'electronics',img: elec5 },
  { id: 16, category: 'electronics', img: elec6 },
  { id: 17, category: 'electronics', img: elec7 },
  { id: 18, category: 'electronics', img: elec8},
  { id: 19, category: 'electronics', img: "https://picsum.photos/200/200" },
  { id: 20, category: 'electronics', img: "https://picsum.photos/200/200" },

  { id: 21, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 22, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 23, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 24, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 25, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 26, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 27, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 28, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 29, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 30, category: 'accessories', img: "https://picsum.photos/200/200" }
];

const ProductList = () => {
  const [menu, setMenu] = useState('all');

  // Filter the products based on the selected category
  const filteredProducts = menu === 'all' ? products : products.filter(product => product.category === menu);

  return (
    <>
      <div className="containerP container">
        <ul className="list">
          <li>
            <a href="#" className={menu === 'all' ? 'active' : ''} onClick={() => setMenu('all')}>
              All
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'electronics' ? 'active' : ''} onClick={() => setMenu('electronics')}>
              Electronics
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'clothing' ? 'active' : ''} onClick={() => setMenu('clothing')}>
              Clothing
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'accessories' ? 'active' : ''} onClick={() => setMenu('accessories')}>
              Accessories
            </a>
          </li>
        </ul>
      </div>

      <div className="image-container">
        {filteredProducts.map(product => (
          <img key={product.id} src={product.img} alt="Product" className="product-image" />
        ))}
      </div>
    </>
  );
};

export default ProductList;
