import React from 'react'
import { Slide } from 'react-slideshow-image'
import pic1 from "./office-1834294_1280.jpg"
import pic2 from "./painting-mountain-lake-with-mountain-background.jpg"
import pic3 from "./travel1.jpg"
import 'react-slideshow-image/dist/styles.css';

const slideImages=[
  {
    url:pic1,
    caption:'slide1',
  },
  {
    url:pic2,
    caption:'slide2',
  }
  , {
    url:pic3,
    caption:'slide3',
  },
];
const divStyle={
  display:"flex",
  alignItems:'center',
  justifyContent:'center',
  backgroundSize:'cover',
  height:'400px',
};
const spanStyle={
  color:'#fff',
  background:'rgba(0,0,0,0.5)',
  padding:'10px',
  borderRadius:'5px',
};

const Learn = () => {
  return (
    <div className="slide-container">
      <Slide>
        {slideImages.map((slideImage,index)=>{
          <div key={index}>
            <div style={{...divStyle,backgroundImage:`url(${slideImage.url})`}}>
              <span style={spanStyle}>{slideImageImages.caption}</span>
            </div>
          </div>
        })}
      </Slide>
    </div>
  )
}

export default Learn
