import React from 'react';
import 'react-slideshow-image/dist/styles.css';
import { Slide } from 'react-slideshow-image';
import pic1 from "../office-1834294_1280.jpg";
import pic2 from "../painting-mountain-lake-with-mountain-background.jpg";
import pic3 from "../travel1.jpg";

const slideImages = [
  {
    url: pic1,
    caption: 'Slide 1',
  },
  {
    url: pic2,
    caption: 'Slide 2',
  },
  {
    url: pic3,
    caption: 'Slide 3',
  },
];

const divStyle = {
  display: "flex",
  alignItems: 'center',
  justifyContent: 'center',
  backgroundSize: 'cover',
  height: '400px',
};

const spanStyle = {
  color: '#fff',
  background: 'rgba(0, 0, 0, 0.5)',
  padding: '10px',
  borderRadius: '5px',
};

const SlideShow = () => {
  return (
    <div className="slide-container">
      <Slide>
        {slideImages.map((slideImage, index) => (
          <div key={index}>
            <div style={{ ...divStyle, backgroundImage: `url(${slideImage.url})` }}>
              <span style={spanStyle}>{slideImage.caption}</span>
            </div>
          </div>
        ))}
      </Slide>
    </div>
  );
}

export default SlideShow;
