import React, { useState } from 'react';
import "./productLIst.css";

import screen1 from "../images/tab.jpg";
import screen2 from "../images/tab1.jpg";
import screen3 from "../images/tab2.jpg";
import screen4 from "../images/tab3.jpg";
import screen5 from "../images/laptop.jpg";
import screen6 from "../images/tv.jpg";

import camera from "../images/camera-510530_640.jpg";
import camera2 from "../images/camera.jpg";
import camera3 from "../images/camera-1248682_640.jpg";
import camera4 from "../images/camera-431119_640.jpg"

import ac from "../images/headphone.jpg";
import ac2 from "../images/watch.jpg";
import ac3 from "../images/speaker.jpg";
import ac4 from "../images/organ-168220_640.jpg"
import ac5 from "../images/keyboard-428324_640.jpg"
import ac6 from "../images/mic-1132528_640.jpg"

const products = [

  { id: 1, category: 'clothing', img:camera , price:"300$" , name:"camera sony"},
  { id: 2, category: 'clothing', img: camera2 ,  price:"400$", name:"camera sony" },
  { id: 3, category: 'clothing', img: camera3 , price:"600$",},
  { id: 4, category: 'clothing', img:camera4 , price:"1000$"},

  { id: 11, category: 'electronics', img: screen1 ,  price:"1200$" },
  { id: 12, category: 'electronics', img: screen2,  price:"3400$"},
  { id: 13, category: 'electronics', img: screen3,  price:"3600$"},
  { id: 14, category: 'electronics', img: screen4, price:"2300$"},
  { id: 15, category: 'electronics',img: screen5, price:"900$"},
  { id: 16, category: 'electronics', img: screen6, price:"500$"},

  { id: 21, category: 'accessories', img:ac ,  price:"100$" },
  { id: 22, category: 'accessories', img: ac2 ,  price:"200$" },
  { id: 23, category: 'accessories', img: ac3 , price:"500$"},
  { id: 24, category: 'accessories', img: ac4 , price:"600$"},
  { id: 25, category: 'accessories', img: ac5 , price:"300$"},
  { id: 26, category: 'accessories', img: ac6 , price:"700$"},
];

const ProductList = () => {
  const [menu, setMenu] = useState('all');

  // Filter the products based on the selected category
  const filteredProducts = menu === 'all' ? products : products.filter(product => product.category === menu);

  return (
    <>
      <div className="containerP container">
        <ul className="list">
          <li>
            <a href="#" className={menu === 'all' ? 'active' : ''} onClick={() => setMenu('all')}>
              All
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'electronics' ? 'active' : ''} onClick={() => setMenu('electronics')}>
              Screens
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'clothing' ? 'active' : ''} onClick={() => setMenu('clothing')}>
              Cameras
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'accessories' ? 'active' : ''} onClick={() => setMenu('accessories')}>
              Accessories
            </a>
          </li>
        </ul>
      </div>

      <div className="image-container">
        {filteredProducts.map(product => (
          <>
          <div>
          <img key={product.id} src={product.img} alt="Product" className="product-image" />
          </div>
          <div>
            <label></label>
            <span>{product.price}</span>
          </div>
          </>
        ))}
      </div>
    </>
  );
};

export default ProductList;
