import React, { useState } from 'react';
import "./productLIst.css";

import screen1 from "../images/tab.jpg";
import screen2 from "../images/tab1.jpg";
import screen3 from "../images/tab2.jpg";
import screen4 from "../images/tab3.jpg";
import screen5 from "../images/laptop.jpg";
import screen6 from "../images/tv.jpg";

import camera from "../images/camera-510530_640.jpg";
import camera2 from "../images/camera.jpg";

import ac from "../images/headphone.jpg";
import ac2 from "../images/watch.jpg";
import ac3 from "../images/speaker.jpg";

const products = [

  { id: 1, category: 'clothing', img:camera },
  { id: 2, category: 'clothing', img: camera2 },
  { id: 3, category: 'clothing', img: camera },
  { id: 4, category: 'clothing', img:camera2 },

  { id: 11, category: 'electronics', img: screen1 },
  { id: 12, category: 'electronics', img: screen2},
  { id: 13, category: 'electronics', img: screen3},
  { id: 14, category: 'electronics', img: screen4},
  { id: 15, category: 'electronics',img: screen5},
  { id: 16, category: 'electronics', img: screen6 },

  { id: 21, category: 'accessories', img:ac },
  { id: 22, category: 'accessories', img: ac2 },
  { id: 23, category: 'accessories', img: ac3 },
  { id: 24, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 25, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 26, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 27, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 28, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 29, category: 'accessories', img: "https://picsum.photos/200/200" },
  { id: 30, category: 'accessories', img: "https://picsum.photos/200/200" }
];

const ProductList = () => {
  const [menu, setMenu] = useState('all');

  // Filter the products based on the selected category
  const filteredProducts = menu === 'all' ? products : products.filter(product => product.category === menu);

  return (
    <>
      <div className="containerP container">
        <ul className="list">
          <li>
            <a href="#" className={menu === 'all' ? 'active' : ''} onClick={() => setMenu('all')}>
              All
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'electronics' ? 'active' : ''} onClick={() => setMenu('electronics')}>
              Electronics
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'clothing' ? 'active' : ''} onClick={() => setMenu('clothing')}>
              Clothing
            </a>
          </li>
          <li>
            <a href="#" className={menu === 'accessories' ? 'active' : ''} onClick={() => setMenu('accessories')}>
              Accessories
            </a>
          </li>
        </ul>
      </div>

      <div className="image-container">
        {filteredProducts.map(product => (
          <img key={product.id} src={product.img} alt="Product" className="product-image" />
        ))}
      </div>
    </>
  );
};

export default ProductList;
