import React from 'react'
import pic1 from "../office-1834294_1280.jpg";
import pic2 from "../painting-mountain-lake-with-mountain-background.jpg";
import pic3 from "../travel1.jpg";
export const data =[
  {
    img:pic1,
    title:"Choose what to Do",
    desc:"Lorem ipsum dolor sit amet, consecte adipisicing elit, sed do eiusmod tempor incididunt ut laboremagna aliqua.",
  },
  {
      img:pic2,
      title:"Find what you want",
      desc:"Lorem ipsum dolor sit amet, consecte adipisicing elit, sed do eiusmod tempor incididunt ut laboremagna aliqua.",
    },
    {
      img:pic3,
      title:"Explore amazing Place",
      desc:"Lorem ipsum dolor sit amet, consecte adipisicing elit, sed do eiusmod tempor incididunt ut laboremagna aliqua.",
    },
];

const HowItWorks = () => {
  return (
    <>
    <div className="text">
    <h2>How It Works</h2>
    <p>Learn More about how our website works</p>
    </div>
    <div className="boxCont">
        <div className="box-2">
         {data.map((index,prop)=>{
          return(
          <div key="index">
          <img src={prop.img} alt="img"/>
          <h1>{prop.title}</h1>
          <p>{prop.desc}</p>
          </div>
          )
         })}
        </div>
    </div>
    </>

  )
}

export default HowItWorks
